-- Analysis of Uber's Operational Data --use uber_analysis-- Data Cleaning ---- checking for null values --select count(*) from rideswhere ride_id is nullselect count(*) from driverwhere driver_id is nullselect count(*) from citywhere city_id is nullselect count(*) from paymentwhere payment_id is null-- Note: ride_id & driver_id has 25 & 27 null values respectively, while no nulls found in city_id & payment_id --SELECT ride_status, COUNT(*) AS cntFROM ridesWHERE ride_id IS NULL OR driver_id IS NULLGROUP BY ride_status;-- Note: Out of the rides with missing ride_id or driver_id, 11 are cancelled and 14 are marked as completed,Cancelled rides with missing identifiers can be safely excluded from analysis --select ride_id, count(*) as cntfrom ridesgroup by ride_idhaving count(*)>1;-- There are 25 duplicate values select distinct(select ride_id, count(*) as cntfrom ridesgroup by ride_idhaving count(*)>1) from rides